-- Create the database
CREATE DATABASE covid19;
USE covid19;

-- Create countries table
CREATE TABLE countries (
    id INT PRIMARY KEY AUTO_INCREMENT,
    name VARCHAR(100),
    region VARCHAR(100)
);

-- Create statistics table
CREATE TABLE statistics (
    id INT PRIMARY KEY AUTO_INCREMENT,
    country_id INT,
    date DATE,
    confirmed INT,
    deaths INT,
    recovered INT,
    FOREIGN KEY (country_id) REFERENCES countries(id)
);

